mapper.select:
select article_id, plan_idx, img_src, img_id, place_name, place_id, date, timeStart, timeEnd, description
from plan_board
where article_id = #{id}

select distinct b.article_id, b.title, b.img, b.name, f.board_type_id, f.content, b.reg_date from board b join free_board f on b.article_id = f.article_id join hash_tag ht on b.article_id = ht.article_id join tags t on ht.tag_id = t.tag_id where b.board_type_id = 1
and (
t.tag_name like concat('%', #{keyword}, '%')
)
order by b.article_id desc;

select b.article_id, b.title, b.img, b.name, b.board_type_id, b.reg_date from board b join hash_tag ht on b.article_id = ht.article_id join tags t on ht.tag_id = t.tag_id where b.board_type_id = 2
and (
t.tag_name like concat('%', #{keyword}, '%')
)
order by b.article_id desc;
